import UIKit

@objc class HotelsConfigurator: NSObject {
    
    @objc static func setupArch(viewController: HotelsViewController) {
        
        let presenter: HotelsPresenter = HotelsPresenter(viewController: viewController)
        let interactor: HotelsInteractor = HotelsInteractor(presenter: presenter, worker: HotelsWorker())
        let router: HotelsRouter = HotelsRouter(viewController: viewController, dataStore: interactor)
        
        viewController.interactor = interactor
        viewController.router = router
    }
}
