import UIKit

protocol HotelsBusinessLogic {
    
    func doSomething(request: Hotels.Something.Request)
}

protocol HotelsDataStore {
    
    //var name: String { get set }
}

class HotelsInteractor: HotelsBusinessLogic, HotelsDataStore {
    
    var presenter: HotelsPresentationLogic?
    var worker: HotelsWorkerProtocol?
    //var name: String = ""
    
    init(presenter: HotelsPresentationLogic, worker: HotelsWorkerProtocol) {
        
        self.presenter = presenter
        self.worker = worker
    }
    
    // MARK: Do something
    
    func doSomething(request: Hotels.Something.Request) {

        let response = Hotels.Something.Response()
        presenter?.presentSomething(response: response)
    }
}
