import UIKit

protocol HotelsPresentationLogic {
    
    func presentSomething(response: Hotels.Something.Response)
}

class HotelsPresenter: HotelsPresentationLogic {
    
    weak var viewController: HotelsDisplayLogic?
    
    init(viewController: HotelsDisplayLogic) {
        
        self.viewController = viewController
    }
    
    // MARK: Do something
    
    func presentSomething(response: Hotels.Something.Response) {
        
        let viewModel = Hotels.Something.ViewModel()
        viewController?.displaySomething(viewModel: viewModel)
    }
}
