import UIKit

@objc class ProductsConfigurator: NSObject {
    
    @objc static func setupArch(viewController: ProductsViewController) {
        
        let presenter: ProductsPresenter = ProductsPresenter(viewController: viewController)
        let interactor: ProductsInteractor = ProductsInteractor(presenter: presenter, worker: ProductsWorker())
        let router: ProductsRouter = ProductsRouter(viewController: viewController, dataStore: interactor)
        
        viewController.interactor = interactor
        viewController.router = router
    }
}
